import PropTypes from 'prop-types';
import { useEffect } from 'react';
import { sendEvent } from '../../helper/EventTracker';

const SocialCounter = ({ social }) => {
  useEffect(() => {
    setInterval(() => {
      if (document.getElementById('socialScroll').scrollTop === 56) {
        document.getElementById('socialScroll').scrollTop = 0;
      } else {
        document.getElementById('socialScroll').scrollTop += 28;
      }
    }, 3000);
  }, []);
  return (
    <div className="h-7 overflow-scroll scroll-smooth" id="socialScroll">
      {Object.keys(social).map((s, i) => {
        if (social[s].followerCount === '') return null;
        return (
          <div
            className="flex justify-end gap-2 items-center text-slate-600 text-xs h-7"
            key={i}
            onClick={() => sendEvent('Click_Socials')}
            id={`social-${i}`}
          >
            <SocialMediaIcons name={s} />
            <p className="counterText">
              <span className="bold">{social[s].followerCount}</span> Followers
            </p>
          </div>
        );
      })}
    </div>
  );
};

SocialCounter.propTypes = {
  social: PropTypes.array,
};

export default SocialCounter;

const SocialMediaIcons = (props) => {
  if (props.name === 'instagram') return <Insta {...props} />;
  if (props.name === 'facebook') return <Facebook {...props} />;
  if (props.name === 'tiktok') return <Tiktok {...props} />;
  if (props.name === 'twitter') return <Twitter {...props} />;
  if (props.name === 'pinterest') return <Pinterest {...props} />;
  if (props.name === 'youtube') return <Youtube {...props} />;
  if (props.name === 'snapchat') return <SnapChat {...props} />;

  return <Insta {...props} />;
};

SocialMediaIcons.propTypes = {
  name: PropTypes.string,
};

